{
    "name": "firstExample",
    "type": "Document",
    "enabled": true,
    "presentation": {
        "layout": "two-column"
    },
    "fields": [
        {
            "name": "string",
            "required": true,
            "multiple": true,
            "type": "String",
            "defaultValue": [
                "Blogtitle1",
                "Blogtitle2"
            ],
            "validations": {
                "maxLength": 10
            },
            "presentation": {
                "caption": "Title",
                "hint": "Hint for string field",
                "layoutColumn": 1,
                "displayType": "Simple"
            }
        },
        {
            "name": "text",
            "required": true,
            "multiple": false,
            "type": "String",
            "defaultValue": [
                "Sample value text field"
            ],
            "validations": {
                "maxRow": 10
            },
            "presentation": {
                "caption": "Text Field",
                "hint": "Hint for text field",
                "layoutColumn": 2,
                "displayType": "Text"
            }
        },
        {
            "name": "date",
            "required": true,
            "type": "Date",
            "defaultValue": [
                "2007-12-03T00:00:00.00Z"
            ],
            "presentation": {
                "caption": "Date Field",
                "hint": "Hint for date field",
                "layoutColumn": 2,
                "timeSelectable": false
            }
        },
        {
            "name": "dateWithTime",
            "required": true,
            "type": "Date",
            "defaultValue": [
                "2007-12-03T10:15:30.00Z"
            ],
            "presentation": {
                "caption": "Date Field",
                "hint": "Hint for date field with time",
                "layoutColumn": 2,
                "timeSelectable": true
            }
        },
        {
            "name": "booleanChekbox",
            "required": true,
            "type": "Boolean",
            "defaultValue": [
                true
            ],
            "presentation": {
                "caption": "Boolean Field",
                "hint": "Hint for boolean field",
                "layoutColumn": 1,
                "displayType": "Checkbox"
            }
        },
        {
            "name": "integer",
            "required": true,
            "type": "Integer",
            "defaultValue": [
                100
            ],
            "presentation": {
                "caption": "Integer Field",
                "hint": "Hint for integer field",
                "layoutColumn": 2
            }
        },
        {
            "name": "number",
            "required": true,
            "multiple": true,
            "type": "Number",
            "defaultValue": [
                100.34,
                2.2
            ],
            "presentation": {
                "caption": "Number Field",
                "hint": "Hint for number field",
                "layoutColumn": 2
            }
        },
        {
            "name": "html",
            "required": true,
            "type": "Html",
            "defaultValue": [
                "<p>Sample content html field</p>"
            ],
            "presentation": {
                "caption": "Html Field",
                "hint": "Hint for html field",
                "layoutColumn": 2,
                "ckEditorAppendedJson": "{\n  \"keystrokes\":[[88,\"showblocks\"]],\n  \"plugins\":\"colorbutton\"\n}",
                "ckEditorOverlayedJson": "{\n  \"codemirror\": {\n    \"autoFormatOnStart\": false\n  },\n  \"ignoreEmptyParagraph\": true\n}"
            }
        },
        {
            "name": "richText",
            "required": false,
            "type": "RichText",
            "defaultValue": [
                "<p>Sample content rich text field</p>"
            ],
            "presentation": {
                "caption": "Content",
                "hint": "Hint for rich text field",
                "layoutColumn": 1,
                "imagepickerType": "images",
                "imagepickerNodetypes": [
                    "folder",
                    "banner"
                ],
                "imagepickerLastVisitedEnabled": true,
                "imagepickerLastVisitedGroupKey": "ckeditor-imagepicker",
                "imagepickerLastVisitedNodetypes": [
                    "gallery",
                    "banner"
                ],
                "imagepickerPreferredImageVariant": "original",
                "imagepickerBasePath": "/content/gallery/brxsaas/images",
                "linkpickerType": "documents",
                "linkpickerNodetypes": [
                    "folder",
                    "banner"
                ],
                "linkpickerLastVisitedEnabled": true,
                "linkpickerLastVisitedGroupKey": "ckeditor-linkpicker",
                "linkpickerLastVisitedNodetypes": [
                    "folder"
                ],
                "linkPickerLanguageContextAware": true,
                "linkPickerOpenInNewWindowEnabled": true
            }
        },
        {
            "name": "dropdownWithStaticValues",
            "required": true,
            "type": "SelectableString",
            "defaultValue": [
                "01"
            ],
            "validations": {
                "inValues": [
                    {
                        "key": "01",
                        "label": "New"
                    },
                    {
                        "key": "02",
                        "label": "Open"
                    },
                    {
                        "key": "03",
                        "label": "Closed"
                    }
                ]
            },
            "presentation": {
                "caption": "Static dropdown Field",
                "hint": "Hint for dropdown field",
                "layoutColumn": 1,
                "displayType": "Dropdown"
            }
        },
        {
            "name": "staticRadiogroupOnBoolean",
            "required": false,
            "type": "Boolean",
            "defaultValue": [
                "false"
            ],
            "validations": {
                "inValues": [
                    "Open",
                    "Closed"
                ]
            },
            "presentation": {
                "caption": "Radiogroup field",
                "hint": "Hint for static radiogroup field on boolean",
                "layoutColumn": 1,
                "displayType": "RadioGroup"
            }
        },
        {
            "name": "imageLink",
            "required": false,
            "type": "Link",
            "defaultValue": [
                "/"
            ],
            "presentation": {
                "caption": "Image",
                "hint": "Hint for image link field",
                "layoutColumn": 1,
                "displayType": "ImageLink",
                "lastVisitedKey": "gallerypicker-imagelink",
                "lastVisitedEnabled": true,
                "uploadEnabled": true
            }
        },
        {
            "name": "link",
            "required": false,
            "type": "Link",
            "defaultValue": [
                "/content/documents/brxsaas/pages/about"
            ],
            "presentation": {
                "caption": "Link",
                "hint": "Hint for link field",
                "layoutColumn": 2,
                "displayType": "AnyLink",
                "lookupFolderPath": "/content/documents/brxsaas/banners",
                "lookupFolderTypes": [
                    "folder"
                ],
                "lastVisitedNodetypes": [
                    "folder"
                ],
                "lastVisitedEnabled": true,
                "languageContextAware": true,
                "pickerType": "documents"
            }
        },
        {
            "name": "resource",
            "required": true,
            "type": "EmbeddedResource",
            "defaultValue": [
                {
                    "filename": "hippo:resource",
                    "binarydata": "",
                    "mimeType": "application/json"
                }
            ],
            "presentation": {
                "caption": "Resource Field",
                "hint": "Hint for resource field",
                "layoutColumn": 2
            }
        }
    ]
}

{
    "name": "secondExample",
    "type": "Document",
    "enabled": true,
    "presentation": {
        "layout": "two-column"
    },
    "fields": [
        {
            "name": "dropdownWithResourceBundle",
            "required": true,
            "type": "SelectableString",
            "defaultValue": [
                "key1"
            ],
            "validations": {
                "inResourceBundle": "brsm.example.bundle"
            },
            "presentation": {
                "caption": "Dynamic dropdown field",
                "hint": "Hint for dynamic dropdown field",
                "layoutColumn": 1,
                "displayType": "Dropdown",
                "sortOrder": "ascending",
                "sortBy": "key",
                "showDefault": true
            }
        },
        {
            "name": "radiogroup",
            "required": true,
            "type": "SelectableString",
            "validations": {
                "inResourceBundle": "brsm.example.bundle"
            },
            "presentation": {
                "caption": "Radiogroup field",
                "hint": "Hint for radio group field",
                "layoutColumn": 1,
                "displayType": "RadioGroup",
                "orientation": "horizontal"
            }
        },
        {
            "name": "radiogroupOnBoolean",
            "required": true,
            "type": "Boolean",
            "defaultValue": [
                "false"
            ],
            "validations": {
                "inResourceBundle": "boolean.resource.bundle"
            },
            "presentation": {
                "caption": "Radiogroup field",
                "hint": "Hint for radiogroup field on boolean",
                "layoutColumn": 1,
                "displayType": "RadioGroup",
                "orientation": "vertical"
            }
        },
        {
            "name": "multiselectfieldWithResourceBundle",
            "required": true,
            "multiple": true,
            "type": "SelectableString",
            "defaultValue": [
                "key1",
                "key2"
            ],
            "validations": {
                "inResourceBundle": "brsm.example.bundle"
            },
            "presentation": {
                "caption": "Multiselect field",
                "hint": "Hint for mmultiselect field with resource bundle",
                "layoutColumn": 1,
                "displayType": "MultiSelect",
                "multiSelectType": "selectlist",
                "maxRowWithoutScrollbar": 2,
                "multiSelectAllowOrder": true
            }
        },
        {
            "name": "taxonomyField",
            "required": true,
            "multiple": true,
            "type": "Taxonomy",
            "defaultValue": [
                "category1"
            ],
            "validations": {
                "inTaxonomy": "testTaxonomy"
            },
            "presentation": {
                "caption": "Taxonomy Field",
                "hint": "Hint for Taxonomy field",
                "layoutColumn": 2
            }
        }
    ]
}

{
    "name": "aFieldGroupType",
    "type": "FieldGroup",
    "enabled": true,
    "presentation": {
        "layout": "one-column"
    },
    "fields": [
        {
            "name": "title",
            "type": "String",
            "required": false,
            "multiple": false,
            "presentation": {
                "caption": "Title",
                "hint": "",
                "layoutColumn": 1,
                "displayType": "Simple"
            },
            "validations": {
                "maxLength": null
            },
            "defaultValue": [
                "A default title"
            ]
        },
        {
            "name": "calendardate",
            "type": "Date",
            "required": false,
            "multiple": false,
            "presentation": {
                "caption": "Calendar Date",
                "hint": "",
                "layoutColumn": 1,
                "timeSelectable": false
            },
            "defaultValue": [
                "2021-10-06T22:00:00Z"
            ]
        }
    ]
}

{
    "name": "aFieldGroupWithFieldGroupType",
    "type": "FieldGroup",
    "enabled": true,
    "presentation": {
        "layout": "one-column"
    },
    "fields": [
        {
            "name": "title",
            "type": "String",
            "required": false,
            "multiple": false,
            "presentation": {
                "caption": "Title",
                "hint": "",
                "layoutColumn": 1,
                "displayType": "Simple"
            },
            "validations": {
                "maxLength": null
            },
            "defaultValue": [
                "Top level title"
            ]
        },
        {
            "name": "aFieldGroupField",
            "type": "FieldGroup",
            "required": false,
            "multiple": false,
            "presentation": {
                "caption": "A FieldGroup field",
                "hint": "",
                "layoutColumn": 1
