}

{
    "name": "thirdExample",
    "type": "Document",
    "enabled": true,
    "presentation": {
        "layout": "two-column"
    },
    "fields": [
        {
            "name": "aFieldGroupField",
            "type": "FieldGroup",
            "required": false,
            "multiple": false,
            "fieldGroupType": "aFieldGroupType",
            "presentation": {
                "caption": "A FieldGroup field",
                "hint": "Hint for FieldGroup field",
                "layoutColumn": 1
            },
            "defaultValue": [
                {
                    "fields": {
                        "title": [
                            "A default title"
                        ],
                        "calendardate": [
                            "2021-10-06T22:00:00Z"
                        ]
                    }
                }
            ]
        },
        {
            "name": "aFieldGroupWithFieldGroupField",
            "type": "FieldGroup",
            "required": false,
            "multiple": false,
            "fieldGroupType": "aFieldGroupWithFieldGroupType",
            "presentation": {
                "caption": "A FieldGroupWithFieldGroup field",
                "hint": "Hint for FieldGroupWithFieldGroup field",
                "layoutColumn": 1
            },
            "defaultValue": [
                {
                    "fields": {
                        "aFieldGroupField": [
                            {
                                "fields": {
                                    "title": [
                                        "A different default title"
                                    ],
                                    "calendardate": [
                                        "2022-02-08T23:00:00Z"
                                    ]
                                }
                            }
                        ],
                        "title": [
                            "Top level title"
                        ]
                    }
                }
            ]
        },
        {
            "name": "aSelectableFieldGroupField",
            "type": "SelectableFieldGroup",
            "required": false,
            "multiple": true,
            "fieldGroupTypes": [
                "aFieldGroupType",
                "aFieldGroupWithFieldGroupType"
            ],
            "presentation": {
                "caption": "A SelectableFieldGroup field",
                "hint": "Hint for SelectableFieldGroup field",
                "layoutColumn": 1,
                "contentPickerType": "links",
                "showFieldGroupNames": false
            },
            "validations": {
                "maxItem": 10
            },
            "defaultValue": [
                {
                    "fieldGroupType": "aFieldGroupType",
                    "fields": {
                        "title": [
                            "A default title"
                        ],
                        "calendardate": [
                            "2021-10-06T22:00:00Z"
                        ]
                    }
                },
                {
                    "fieldGroupType": "aFieldGroupWithFieldGroupType",
                    "fields": {
                        "aFieldGroupField": [
                            {
                                "fields": {
                                    "title": [
                                        "A different default title"
                                    ],
                                    "calendardate": [
                                        "2022-02-08T23:00:00Z"
                                    ]
                                }
                            }
                        ],
                        "title": [
                            "Top level title"
                        ]
                    }
                }
            ]
        }
    ]
}
